SELECT MIN(t.title) AS typical_european_movie FROM postgres.company_type AS ct, postgres.info_type AS it, postgres.movie_companies AS mc, postgres.movie_info AS mi, postgres.title AS t WHERE ct.kind = 'production companies' AND mc.note like '%ase%' and mc.note like '%ase%' AND mi.info IN ( 'Color','Silent','USA','West Germany:18','PFM:35 mm' ) AND t.production_year > 2004 AND t.id = mi.movie_id AND t.id = mc.movie_id AND mc.movie_id = mi.movie_id AND ct.id = mc.company_type_id AND it.id = mi.info_type_id;