SELECT MIN(cn.name) AS from_company, MIN(mc.note) AS production_note, MIN(t.title) AS movie_based_on_book FROM postgres.company_name AS cn, postgres.company_type AS ct, postgres.keyword AS k, postgres.link_type AS lt, postgres.movie_companies AS mc, postgres.movie_keyword AS mk, postgres.movie_link AS ml, postgres.title AS t WHERE cn.country_code <> '[pl]' AND ct.kind <> 'special effects companies' and ct.kind is not NULL AND k.keyword in ('victim-of-a-motorcycle-accident','police-interrogation','envelopment') AND mc.note is not NULL AND t.production_year > 1952 AND lt.id = ml.link_type_id AND ml.movie_id = t.id AND t.id = mk.movie_id AND mk.keyword_id = k.id AND t.id = mc.movie_id AND mc.company_type_id = ct.id AND mc.company_id = cn.id AND ml.movie_id = mk.movie_id AND ml.movie_id = mc.movie_id AND mk.movie_id = mc.movie_id;