SELECT MIN(cn.name) AS producing_company, MIN(lt.link) AS link_type, MIN(t.title) AS complete_western_sequel FROM postgres.complete_cast AS cc, postgres.comp_cast_type AS cct1, postgres.comp_cast_type AS cct2, postgres.company_name AS cn, postgres.company_type AS ct, postgres.keyword AS k, postgres.link_type AS lt, postgres.movie_companies AS mc, postgres.movie_info AS mi, postgres.movie_keyword AS mk, postgres.movie_link AS ml, postgres.title AS t WHERE cct1.kind in ('complete','complete','cast','cast','crew','crew','complete','complete','complete+verified') AND cct2.kind = 'complete' AND cn.country_code <> '[us]' AND ( cn.name LIKE '%m%' OR cn.name LIKE '%m%' ) AND ct.kind = 'distributors' AND k.keyword = 'serial-killer' AND lt.link LIKE '%ed%' AND mc.note IS NULL AND mi.info IN ('Comedy','Mono','English','$12,000','USA:23 October 2012','UK:12A','Color','Tagalog','Italy','USA:11 October 2006') AND t.production_year between 1996 and 1999 AND lt.id = ml.link_type_id AND ml.movie_id = t.id AND t.id = mk.movie_id AND mk.keyword_id = k.id AND t.id = mc.movie_id AND mc.company_type_id = ct.id AND mc.company_id = cn.id AND mi.movie_id = t.id AND t.id = cc.movie_id AND cct1.id = cc.subject_id AND cct2.id = cc.status_id AND ml.movie_id = mk.movie_id AND ml.movie_id = mc.movie_id AND mk.movie_id = mc.movie_id AND ml.movie_id = mi.movie_id AND mk.movie_id = mi.movie_id AND mc.movie_id = mi.movie_id AND ml.movie_id = cc.movie_id AND mk.movie_id = cc.movie_id AND mc.movie_id = cc.movie_id AND mi.movie_id = cc.movie_id;