SELECT MIN(an.name) AS alternative_name, MIN(chn.name) AS character_name, MIN(t.title) AS movie FROM postgres.aka_name AS an, postgres.char_name AS chn, postgres.cast_info AS ci, postgres.company_name AS cn, postgres.movie_companies AS mc, postgres.name AS n, postgres.role_type AS rt, postgres.title AS t WHERE ci.note in ( '(location scout)','(executive producer)','(director of photography)','(uncredited)','(scenario)','(continuity)','(executive vice president: creative affairs)','(office runner)' ) AND cn.country_code = '[us]' AND mc.note is not NULL and ( mc.note like '%(USA)%' or mc.note like '%(worldwide)%' ) AND n.gender = 'f' and n.name like '%Ang%' AND rt.role = 'actress' AND t.production_year between 1953 and 2010 AND ci.movie_id = t.id AND t.id = mc.movie_id AND ci.movie_id = mc.movie_id AND mc.company_id = cn.id AND ci.role_id = rt.id AND n.id = ci.person_id AND chn.id = ci.person_role_id AND an.person_id = n.id AND an.person_id = ci.person_id;