SELECT MIN(cn.name) AS movie_company, MIN(mi_idx.info) AS rating, MIN(t.title) AS complete_euro_dark_movie FROM postgres.complete_cast AS cc, postgres.comp_cast_type AS cct1, postgres.comp_cast_type AS cct2, postgres.company_name AS cn, postgres.company_type AS ct, postgres.info_type AS it1, postgres.info_type AS it2, postgres.keyword AS k, postgres.kind_type AS kt, postgres.movie_companies AS mc, postgres.movie_info AS mi, postgres.movie_info_idx AS mi_idx, postgres.movie_keyword AS mk, postgres.title AS t WHERE cct1.kind = 'cast' AND cct2.kind <> 'cast' AND cn.country_code <> '[us]' AND it1.info = 'LD release date' AND it2.info = 'rating' AND kt.kind in ('video movie','episode','episode','episode','video game','episode','video movie') AND mc.note not like '%1948) (Cuba) (the%' AND mi.info not IN ('German','4','USA:21','PFM:35 mm','Washington, District of Columbia, USA','USA:9 September 2010','Horror','Drama','Drama') AND mi_idx.info > '0000001112' AND t.production_year > 1995 AND kt.id = t.kind_id AND t.id = mi.movie_id AND t.id = mk.movie_id AND t.id = mi_idx.movie_id AND t.id = mc.movie_id AND t.id = cc.movie_id AND mk.movie_id = mi.movie_id AND mk.movie_id = mi_idx.movie_id AND mk.movie_id = mc.movie_id AND mk.movie_id = cc.movie_id AND mi.movie_id = mi_idx.movie_id AND mi.movie_id = mc.movie_id AND mi.movie_id = cc.movie_id AND mc.movie_id = mi_idx.movie_id AND mc.movie_id = cc.movie_id AND mi_idx.movie_id = cc.movie_id AND k.id = mk.keyword_id AND it1.id = mi.info_type_id AND it2.id = mi_idx.info_type_id AND ct.id = mc.company_type_id AND cn.id = mc.company_id AND cct1.id = cc.subject_id AND cct2.id = cc.status_id;