SELECT MIN(t.title) AS movie_title FROM postgres.keyword AS k, postgres.movie_info AS mi, postgres.movie_keyword AS mk, postgres.title AS t WHERE k.keyword like '%sequel%' AND mi.info IN ( 'Germany:2 February 2011','CAM:Sony PMW-EX1','Drama','OFM:35 mm','USA','UK' ) AND t.production_year > 1993 AND t.id = mi.movie_id AND t.id = mk.movie_id AND mk.movie_id = mi.movie_id AND k.id = mk.keyword_id;