SELECT MIN(cn.name) AS movie_company, MIN(mi_idx.info) AS rating, MIN(t.title) AS complete_euro_dark_movie FROM postgres.complete_cast AS cc, postgres.comp_cast_type AS cct1, postgres.comp_cast_type AS cct2, postgres.company_name AS cn, postgres.company_type AS ct, postgres.info_type AS it1, postgres.info_type AS it2, postgres.keyword AS k, postgres.kind_type AS kt, postgres.movie_companies AS mc, postgres.movie_info AS mi, postgres.movie_info_idx AS mi_idx, postgres.movie_keyword AS mk, postgres.title AS t WHERE cct1.kind = 'cast' AND cct2.kind <> 'complete+verified' AND cn.country_code <> '[jp]' AND it1.info = 'color info' AND it2.info = 'bottom 10 rank' AND kt.kind in ('episode','tv mini series','tv series','tv movie','tv mini series','tv movie','tv movie','movie','movie','tv series') AND mc.note not like '%20%' AND mi.info not IN ('Adult','Stereo','Black and White','USA','Greece','Argentina:60','Drama','Short') AND mi_idx.info > '0...002310' AND t.production_year > 1962 AND kt.id = t.kind_id AND t.id = mi.movie_id AND t.id = mk.movie_id AND t.id = mi_idx.movie_id AND t.id = mc.movie_id AND t.id = cc.movie_id AND mk.movie_id = mi.movie_id AND mk.movie_id = mi_idx.movie_id AND mk.movie_id = mc.movie_id AND mk.movie_id = cc.movie_id AND mi.movie_id = mi_idx.movie_id AND mi.movie_id = mc.movie_id AND mi.movie_id = cc.movie_id AND mc.movie_id = mi_idx.movie_id AND mc.movie_id = cc.movie_id AND mi_idx.movie_id = cc.movie_id AND k.id = mk.keyword_id AND it1.id = mi.info_type_id AND it2.id = mi_idx.info_type_id AND ct.id = mc.company_type_id AND cn.id = mc.company_id AND cct1.id = cc.subject_id AND cct2.id = cc.status_id;