SELECT MIN(mi.info) AS release_date, MIN(t.title) AS youtube_movie FROM postgres.aka_title AS at_alias, postgres.company_name AS cn, postgres.company_type AS ct, postgres.info_type AS it1, postgres.keyword AS k, postgres.movie_companies AS mc, postgres.movie_info AS mi, postgres.movie_keyword AS mk, postgres.title AS t WHERE cn.country_code = '[uy]' and cn.name like 'Sony Pictures Releasing%' AND it1.info = 'opening weekend' and mc.note like '%%' AND t.production_year between 2010 and 2013 AND t.id = at.movie_id AND t.id = mi.movie_id AND t.id = mk.movie_id AND t.id = mc.movie_id AND mk.movie_id = mi.movie_id AND mk.movie_id = mc.movie_id AND mk.movie_id = at.movie_id AND mi.movie_id = mc.movie_id AND mi.movie_id = at.movie_id AND mc.movie_id = at.movie_id AND k.id = mk.keyword_id AND it1.id = mi.info_type_id AND cn.id = mc.company_id AND ct.id = mc.company_type_id;