SELECT MIN(mi_idx.info) AS rating, MIN(t.title) AS western_dark_production FROM postgres.info_type AS it1, postgres.info_type AS it2, postgres.keyword AS k, postgres.kind_type AS kt, postgres.movie_info AS mi, postgres.movie_info_idx AS mi_idx, postgres.movie_keyword AS mk, postgres.title AS t WHERE it1.info = 'countries' AND kt.kind = 'tv movie' AND mi_idx.info > '0..0..0115' AND t.production_year > 2009 and ( t.title like '%murder%' or t.title like '%Murde%' or t.title like '%Mord%' ) AND kt.id = t.kind_id AND t.id = mi.movie_id AND t.id = mk.movie_id AND t.id = mi_idx.movie_id AND mk.movie_id = mi.movie_id AND mk.movie_id = mi_idx.movie_id AND mi.movie_id = mi_idx.movie_id AND k.id = mk.keyword_id AND it1.id = mi.info_type_id AND it2.id = mi_idx.info_type_id;