SELECT MIN(lt.link) AS link_type, MIN(t1.title) AS first_movie, MIN(t2.title) AS second_movie FROM postgres.keyword AS k, postgres.link_type AS lt, postgres.movie_keyword AS mk, postgres.movie_link AS ml, postgres.title AS t1, postgres.title AS t2 WHERE k.keyword = 'locked-box' AND mk.keyword_id = k.id AND t1.id = mk.movie_id AND ml.movie_id = t1.id AND ml.linked_movie_id = t2.id AND lt.id = ml.link_type_id AND mk.movie_id = t1.id;