SELECT MIN(k.keyword) AS movie_keyword, MIN(n.name) AS actor_name, MIN(t.title) AS hero_movie FROM postgres.cast_info AS ci, postgres.keyword AS k, postgres.movie_keyword AS mk, postgres.name AS n, postgres.title AS t WHERE k.keyword in ( 'bag-of-money','search-warrant','eviction','islet','damsel-in-distress','steamer','american-resort','indian-attack','shy-girl' ) AND n.name LIKE '%Downey%Robert%' AND t.production_year > 2009 AND k.id = mk.keyword_id AND t.id = mk.movie_id AND t.id = ci.movie_id AND ci.movie_id = mk.movie_id AND n.id = ci.person_id;