SELECT MIN(t.title) AS typical_european_movie FROM postgres.company_type AS ct, postgres.info_type AS it, postgres.movie_companies AS mc, postgres.movie_info AS mi, postgres.title AS t WHERE ct.kind = 'production companies' AND mc.note like '%ll %' and mc.note like '%ll %' AND mi.info IN ( 'Torino, Italy','Color','Action','Color','India' ) AND t.production_year > 1997 AND t.id = mi.movie_id AND t.id = mc.movie_id AND mc.movie_id = mi.movie_id AND ct.id = mc.company_type_id AND it.id = mi.info_type_id;