SELECT MIN(n.name) AS cast_member, MIN(t.title) AS complete_dynamic_hero_movie FROM postgres.complete_cast AS cc, postgres.comp_cast_type AS cct1, postgres.comp_cast_type AS cct2, postgres.char_name AS chn, postgres.cast_info AS ci, postgres.keyword AS k, postgres.kind_type AS kt, postgres.movie_keyword AS mk, postgres.name AS n, postgres.title AS t WHERE cct1.kind = 'cast' AND cct2.kind like '%%' AND chn.name is not NULL and ( chn.name like '%man%' or chn.name like '%Man%' ) AND k.keyword in ( 'accusation','clan','hockey-stick','snitch','feminism' ) AND kt.kind = 'episode' AND t.production_year > 2008 AND kt.id = t.kind_id AND t.id = mk.movie_id AND t.id = ci.movie_id AND t.id = cc.movie_id AND mk.movie_id = ci.movie_id AND mk.movie_id = cc.movie_id AND ci.movie_id = cc.movie_id AND chn.id = ci.person_role_id AND n.id = ci.person_id AND k.id = mk.keyword_id AND cct1.id = cc.subject_id AND cct2.id = cc.status_id;