SELECT MIN(chn.name) AS character_name, MIN(mi_idx.info) AS rating, MIN(t.title) AS complete_hero_movie FROM postgres.complete_cast AS cc, postgres.comp_cast_type AS cct1, postgres.comp_cast_type AS cct2, postgres.char_name AS chn, postgres.cast_info AS ci, postgres.info_type AS it2, postgres.keyword AS k, postgres.kind_type AS kt, postgres.movie_info_idx AS mi_idx, postgres.movie_keyword AS mk, postgres.name AS n, postgres.title AS t WHERE cct1.kind = 'cast' AND cct2.kind like '%e%' AND chn.name is not NULL and ( chn.name like '%aye%' or chn.name like '%aye%' ) AND it2.info = 'rating' AND k.keyword in ( 'eccentric','thirst','dancing','liar','cashing-check','stock','fictional-war','shooting','hit-by-a-car' ) AND kt.kind = 'movie' AND t.production_year > 1998 AND kt.id = t.kind_id AND t.id = mk.movie_id AND t.id = ci.movie_id AND t.id = cc.movie_id AND t.id = mi_idx.movie_id AND mk.movie_id = ci.movie_id AND mk.movie_id = cc.movie_id AND mk.movie_id = mi_idx.movie_id AND ci.movie_id = cc.movie_id AND ci.movie_id = mi_idx.movie_id AND cc.movie_id = mi_idx.movie_id AND chn.id = ci.person_role_id AND n.id = ci.person_id AND k.id = mk.keyword_id AND cct1.id = cc.subject_id AND cct2.id = cc.status_id AND it2.id = mi_idx.info_type_id;