SELECT MIN(mc.note) AS production_note, MIN(t.title) AS movie_title, MIN(t.production_year) AS movie_year FROM postgres.company_type AS ct, postgres.info_type AS it, postgres.movie_companies AS mc, postgres.movie_info_idx AS mi_idx, postgres.title AS t WHERE ct.kind = 'distributors' AND it.info = 'top 250 rank' AND mc.note not like '%rlands)%' AND t.production_year between 1978 and 2005 AND ct.id = mc.company_type_id AND t.id = mc.movie_id AND t.id = mi_idx.movie_id AND mc.movie_id = mi_idx.movie_id AND it.id = mi_idx.info_type_id;