SELECT MIN(kt.kind) AS movie_kind, MIN(t.title) AS complete_nerdy_internet_movie FROM postgres.complete_cast AS cc, postgres.comp_cast_type AS cct1, postgres.company_name AS cn, postgres.company_type AS ct, postgres.info_type AS it1, postgres.keyword AS k, postgres.kind_type AS kt, postgres.movie_companies AS mc, postgres.movie_info AS mi, postgres.movie_keyword AS mk, postgres.title AS t WHERE cct1.kind = 'complete' AND cn.country_code = '[au]' AND it1.info = 'release dates' AND k.keyword in ('miracle','werner-erhard','undercover-marshal','missing-bus','severed-finger') AND kt.kind in ('tv mini series','tv series','movie','video movie','episode','tv mini series','video movie','video movie') AND mi.note like '%%' AND mi.info like '%a%' AND t.production_year > 1997 AND kt.id = t.kind_id AND t.id = mi.movie_id AND t.id = mk.movie_id AND t.id = mc.movie_id AND t.id = cc.movie_id AND mk.movie_id = mi.movie_id AND mk.movie_id = mc.movie_id AND mk.movie_id = cc.movie_id AND mi.movie_id = mc.movie_id AND mi.movie_id = cc.movie_id AND mc.movie_id = cc.movie_id AND k.id = mk.keyword_id AND it1.id = mi.info_type_id AND cn.id = mc.company_id AND ct.id = mc.company_type_id AND cct1.id = cc.status_id;